files.template:
receivers: { otlp: { protocols: { http: {} } } }
processors: { batch: {} }
exporters: { logging: {}, otlphttp/traces: { endpoint: http://tempo:3200 }, otlphttp/logs: { endpoint: http://loki:3100/otlp }, prometheus: { endpoint: '0.0.0.0:9464' } }
extensions: { health_check: {} }
service:
  extensions: [health_check]
  pipelines:
    traces: { receivers:[otlp], processors:[batch], exporters:[logging, otlphttp/traces] }
    logs: { receivers:[otlp], processors:[batch], exporters:[logging, otlphttp/logs] }
    metrics: { receivers:[otlp], processors:[batch], exporters:[prometheus] }
