const useOtlp = process.env.OTLP === '1'; const useProm = process.env.PROM === '1';
const { NodeTracerProvider } = require('@opentelemetry/sdk-trace-node');
const { SimpleSpanProcessor, ConsoleSpanExporter, BatchSpanProcessor } = require('@opentelemetry/sdk-trace-base');
const { MeterProvider, PeriodicExportingMetricReader, ConsoleMetricExporter } = require('@opentelemetry/sdk-metrics');
const { OTLPTraceExporter } = require('@opentelemetry/exporter-trace-otlp-http');
const { PrometheusExporter } = require('@opentelemetry/exporter-prometheus');
const tracerProvider = new NodeTracerProvider();
if (useOtlp) tracerProvider.addSpanProcessor(new BatchSpanProcessor(new OTLPTraceExporter({ url: process.env.OTLP_URL || 'http://localhost:4318/v1/traces' })));
else tracerProvider.addSpanProcessor(new SimpleSpanProcessor(new ConsoleSpanExporter()));
tracerProvider.register();
const meterProvider = new MeterProvider();
if (useProm) meterProvider.addMetricReader(new PrometheusExporter({ port: 9464, endpoint: '/metrics' }));
else meterProvider.addMetricReader(new PeriodicExportingMetricReader({ exporter: new ConsoleMetricExporter(), exportIntervalMillis: 2000 }));
module.exports = { tracer: require('@opentelemetry/api').trace.getTracer('switch'), meter: meterProvider.getMeter('switch') };

const { tracer, meter } = require('./otel/init');
const pino = require('pino')();
const express = require('express');
const app = express();
const counter = meter.createCounter('requests');
app.get('/ping', (_req,res)=> { counter.add(1); const span = tracer.startSpan('ping'); setTimeout(()=>{ span.end(); pino.info('ping'); res.json({ ok: true }); }, 100); });
app.listen(3000, ()=> pino.info('listening on 3000'));
