const { MeterProvider } = require('@opentelemetry/sdk-metrics');
const { PrometheusExporter } = require('@opentelemetry/exporter-prometheus');
const exporter = new PrometheusExporter({ port: 9464, endpoint: '/metrics' }, ()=> console.log('scrape http://localhost:9464/metrics'));
const meterProvider = new MeterProvider();
meterProvider.addMetricReader(exporter);
const meter = meterProvider.getMeter('demo');
const counter = meter.createCounter('demo_counter');
setInterval(()=> counter.add(1), 1000);
