function setup(){ createCanvas(400,400); noCursor(); }
function draw(){ background(240); fill('#1e90ff'); noStroke(); circle(mouseX, mouseY, 40); fill(30); textSize(12); text('Press SPACE to pause/resume', 10, height-12);}
function keyPressed(){ if(key===' '){ (isLooping()? noLoop(): loop()); } }
