const { Pact } = require('@pact-foundation/pact');
const path = require('path');
const provider = new Pact({ consumer: 'web', provider: 'api', dir: path.resolve(__dirname, 'pacts') });
test('contract', async () => {
  await provider.setup();
  await provider.addInteraction({ state: 'user exists', uponReceiving: 'a request', withRequest: { method: 'GET', path: '/user' }, willRespondWith: { status: 200, body: { id: 1 } } });
  // call provider.mockService.baseUrl() here
  await provider.verify();
  await provider.finalize();
});
