const paypal = require('@paypal/checkout-server-sdk');
const env = new paypal.core.SandboxEnvironment(process.env.PAYPAL_ID, process.env.PAYPAL_SECRET);
const client = new paypal.core.PayPalHttpClient(env);
const req = new paypal.orders.OrdersCreateRequest();
req.requestBody({ intent: 'CAPTURE', purchase_units: [{ amount: { currency_code: 'USD', value: '10.00' } }] });
client.execute(req).then(r=>console.log(r.result.id));
