const { pbkdf2Sync, randomBytes, timingSafeEqual } = require('node:crypto');
const salt = randomBytes(16);
const hash = pbkdf2Sync('secret', salt, 310000, 32, 'sha256');
console.log(hash.toString('hex'), salt.toString('hex'));
console.log(timingSafeEqual(hash, pbkdf2Sync('secret', salt, 310000, 32, 'sha256')));
