import pg from 'pg';
const client = new pg.Client({ connectionString: process.env.DATABASE_URL || '{{database_url}}' });
await client.connect();
await client.query('CREATE EXTENSION IF NOT EXISTS vector');
await client.query('DROP TABLE IF EXISTS items');
await client.query('CREATE TABLE items (id serial primary key, embedding vector(3), text text)');
await client.query('INSERT INTO items (embedding, text) VALUES ($1,$2),($3,$4)', [[0.1,0.2,0.3], 'hello', [0.2,0.1,0.25], 'fluorite']);
await client.query('CREATE INDEX IF NOT EXISTS items_embedding_hnsw ON items USING hnsw (embedding vector_ip_ops) WITH (m = 8, ef_construction = 32)');
const { rows } = await client.query('SELECT text, embedding <#> $1 AS ip FROM items ORDER BY ip ASC LIMIT 2', [[0.1,0.2,0.3]]);
console.log(rows.map(r => r.text));
await client.end();
