import { Pinecone } from '@pinecone-database/pinecone';
const pc = new Pinecone({ apiKey: process.env.PINECONE_API_KEY });
const name = process.env.PINECONE_INDEX || '{{index_name}}';
const dim = Number(process.env.PINECONE_DIM || '{{dimension}}');
try { await pc.createIndex({ name, dimension: dim, metric: 'cosine' }); console.log('created', name); } catch (e) { console.log('create failed', e?.message); }
console.log(await pc.listIndexes());
