import { Pinecone } from '@pinecone-database/pinecone';
const pc = new Pinecone({ apiKey: process.env.PINECONE_API_KEY });
const index = pc.Index(process.env.PINECONE_INDEX || '{{pinecone_index}}');
await index.upsert([{ id: '1', values: [0.1,0.2,0.3], metadata: { tag: 'a', text: 'hello' } }, { id: '2', values: [0.2,0.1,0.25], metadata: { tag: 'b', text: 'fluorite' } }]);
const res = await index.query({ vector: [0.1,0.2,0.3], topK: 3, includeMetadata: true, filter: { tag: { $eq: 'a' } } });
console.log(res.matches?.map(m => m.metadata?.text));
