import { Pinecone } from '@pinecone-database/pinecone';
const pc = new Pinecone({ apiKey: process.env.PINECONE_API_KEY });
const index = pc.Index(process.env.PINECONE_INDEX || '{{pinecone_index}}');
await index.upsert([{ id: '1', values: [0.1,0.2,0.3], metadata: { score: 0.7 } }, { id: '2', values: [0.2,0.1,0.25], metadata: { score: 0.3 } }]);
const res = await index.query({ vector: [0.1,0.2,0.3], topK: 2, includeMetadata: true, filter: { score: { $gte: 0.5 } } });
console.log(res.matches?.map(m => m.id));
