import { Pinecone } from '@pinecone-database/pinecone';
const pc = new Pinecone({ apiKey: process.env.PINECONE_API_KEY });
const index = pc.Index(process.env.PINECONE_INDEX || '{{pinecone_index}}');
const ns = process.env.PINECONE_NAMESPACE || '{{pinecone_namespace}}';
await index.namespace(ns).upsert([{ id: '1', values: [0.1,0.2,0.3], metadata: { text: 'hello' } }]);
const res = await index.namespace(ns).query({ vector: [0.1,0.2,0.3], topK: 3, includeMetadata: true });
console.log(res.matches?.map(m => m.metadata?.text));
