const { PostHog } = require('posthog-node');
const ph = new PostHog(process.env.POSTHOG_API_KEY||'', { host: process.env.POSTHOG_HOST||'https://app.posthog.com' });
(async () => { await ph.capture({ event: 'test', distinctId: 'user1' }); await ph.shutdown(); console.log('sent'); })();
