const { ServerClient } = require('postmark');
const client = new ServerClient(process.env.POSTMARK_API_TOKEN||'');
(async () => { const r = await client.sendEmail({ From: 'no-reply@example.com', To: 'user@example.com', Subject: 'Hello', TextBody: 'Hello Postmark' }); console.log(r.Message); })();
