const express = require('express');
const crypto = require('crypto');
const app = express();
app.use(express.raw({ type: '*/*' }));
app.post('/prismic', (req,res)=>{ const sig = req.headers['x-prismic-signature']; const h = crypto.createHmac('sha1', process.env.PRISMIC_SECRET).update(req.body).digest('hex'); res.status(sig===h?200:401).send('ok'); });
app.listen(3000, ()=>console.log('listening'));
