const express = require('express');
const client = require('prom-client');
const app = express();
const counter = new client.Counter({ name: 'app_requests_total', help: 'Total requests' });
app.get('/', (_req,res)=>{ counter.inc(); res.json({ ok: true }); });
app.get('/metrics', async (_req,res)=>{ res.set('Content-Type', client.register.contentType); res.end(await client.register.metrics()); });
app.listen(Number('{{port}}'), ()=>console.log('listening on {{port}}'));
