const express = require('express');
const client = require('prom-client');
const app = express();
const h = new client.Histogram({ name: 'http_request_duration_seconds', help: 'duration', buckets: [0.05,0.1,0.25,0.5,1,2] });
app.use((req,res,next)=>{ const end = h.startTimer(); res.on('finish', ()=>end()); next(); });
app.get('/', (_req,res)=>res.send('ok'));
app.get('/metrics', async (_req,res)=>{ res.set('Content-Type', client.register.contentType); res.end(await client.register.metrics()); });
app.listen(Number('{{port}}'));
