const express = require('express');
const client = require('prom-client');
const app = express();
const counter = new client.Counter({ name: 'hits_total', help: 'hits' });
app.get('/', (_req, res) => { counter.inc(); res.send('ok'); });
app.get('/metrics', async (_req, res) => { res.set('Content-Type', client.register.contentType); res.send(await client.register.metrics()); });
app.listen(3000);
