const Pulsar = require('pulsar-client');
(async () => {
  const client = new Pulsar.Client({ serviceUrl: process.env.PULSAR_URL || 'pulsar://localhost:6650' });
  const producer = await client.createProducer({ topic: process.env.PULSAR_TOPIC || '{{topic}}' });
  await producer.send({ data: Buffer.from('hello') });
  await producer.close();
  await client.close();
})();
