import * as aws from '@pulumi/aws';
const role = new aws.iam.Role('r', { assumeRolePolicy: aws.iam.assumeRolePolicyForPrincipal({ Service: 'lambda.amazonaws.com' }) });
new aws.lambda.Function('fn', { role: role.arn, runtime: 'nodejs18.x', handler: 'index.handler', code: new aws.s3.BucketObjectv2('code',{ bucket: 'b', content: 'x' }).bucket });
