import * as aws from '@pulumi/aws';
const subnet = new aws.ec2.DefaultSubnet('defaultSubnet', { availabilityZone: 'us-east-1a' });
const sg = new aws.ec2.SecurityGroup('db-sg', { ingress: [{ protocol: 'tcp', fromPort: 5432, toPort: 5432, cidrBlocks: ['0.0.0.0/0'] }] });
const db = new aws.rds.Instance('db', { engine: 'postgres', instanceClass: 'db.t3.micro', allocatedStorage: 20, username: 'postgres', password: 'postgrespostgres', skipFinalSnapshot: true, vpcSecurityGroupIds: [sg.id], dbSubnetGroupName: undefined });
export const endpoint = db.address;
