import * as k8s from '@pulumi/kubernetes';
const ns = new k8s.core.v1.Namespace('demo');
new k8s.networking.v1.Ingress('app', { metadata:{ namespace: ns.metadata.name }, spec:{ rules:[{ host:'example.com', http:{ paths:[{ path:'/', pathType:'Prefix', backend:{ service:{ name:'app', port:{ number:80 } } } }] } }] } });
