import { QdrantClient } from '@qdrant/js-client-rest';
const client = new QdrantClient({ url: process.env.QDRANT_URL || '{{qdrant_url}}' });
const collection = process.env.QDRANT_COLLECTION || '{{qdrant_collection}}';
await client.upsert(collection, { points: [
  { id: '1', vector: [0.1,0.2,0.3], payload: { text: 'hello world' } },
  { id: '2', vector: [0.2,0.1,0.25], payload: { text: 'fluorite mcp' } }
] });
console.log('Batch upsert done');
