import { QdrantClient } from '@qdrant/js-client-rest';
const client = new QdrantClient({ url: process.env.QDRANT_URL || '{{qdrant_url}}' });
const collection = process.env.QDRANT_COLLECTION || '{{qdrant_collection}}';
await client.upsert(collection, { points: [
  { id: '1', vector: [0.1,0.2,0.3], payload: { tag: 'a' } },
  { id: '2', vector: [0.2,0.1,0.25], payload: { tag: 'b' } },
  { id: '3', vector: [0.1,0.25,0.2], payload: { tag: 'c' } }
] });
const filter = {
  must: [{ key: 'tag', match: { value: 'a' } }],
  should: [{ key: 'tag', match: { value: 'b' } }],
  must_not: [{ key: 'tag', match: { value: 'c' } }]
};
const res = await client.search(collection, { vector: [0.1,0.2,0.3], limit: 3, filter });
console.log(res.map(p => p.id));
