import { QdrantClient } from '@qdrant/js-client-rest';
const client = new QdrantClient({ url: process.env.QDRANT_URL || '{{qdrant_url}}' });
await client.deleteSnapshot(process.env.QDRANT_COLLECTION || '{{qdrant_collection}}', process.env.QDRANT_SNAPSHOT || '{{snapshot}}');
console.log('deleted');
