import { QdrantClient } from '@qdrant/js-client-rest';
const client = new QdrantClient({ url: process.env.QDRANT_URL || '{{qdrant_url}}' });
const collection = process.env.QDRANT_COLLECTION || '{{qdrant_collection}}';
await client.upsert(collection, { points: [{ id: '1', vector: [0.1,0.2,0.3], payload: { score: 0.7 } }, { id: '2', vector: [0.2,0.1,0.25], payload: { score: 0.3 } }] });
const res = await client.search(collection, { vector: [0.1,0.2,0.3], limit: 2, filter: { must: [{ key: 'score', range: { gte: 0.5 } }] } });
console.log(res.map(p => p.id));
