import { QdrantClient } from '@qdrant/js-client-rest';
const client = new QdrantClient({ url: process.env.QDRANT_URL || '{{qdrant_url}}' });
const collection = process.env.QDRANT_COLLECTION || '{{qdrant_collection}}';
await client.upsert(collection, { points: [{ id: '1', vector: [0.1,0.2,0.3] }] });
await client.upsert(collection, { points: [{ id: '1', vector: [0.2,0.1,0.25] }] });
const res = await client.search(collection, { vector: [0.2,0.1,0.25], limit: 1 });
console.log(res.map(p => p.id));
