import { QdrantClient } from '@qdrant/js-client-rest';
const client = new QdrantClient({ url: process.env.QDRANT_URL || '{{qdrant_url}}' });
const collection = process.env.QDRANT_COLLECTION || '{{qdrant_collection}}';
let next = null;
const out = [];
for (let i = 0; i < 3; i++) {
  const res = await client.scroll(collection, { limit: 2, with_payload: true, offset: next?.offset, page: next?.page });
  out.push(...(res.points || []));
  if (!res.next_page_offset) break;
  next = { offset: res.next_page_offset, page: (next?.page || 1) + 1 };
}
console.log(out.length);
