const { QdrantClient } = require('qdrant-node');
(async()=>{ const c = new QdrantClient({ url: process.env.QDRANT_URL||'http://localhost:6333' });
const r = await c.search(process.env.QDRANT_COLLECTION||'{{collection}}', { vector:[0.1,0.2,0.3], limit:1, filter: { must: [{ key:'lang', match:{ value:'en' } }] } });
console.log(r.length); })();
