import { QdrantClient } from '@qdrant/js-client-rest';
const client = new QdrantClient({ url: process.env.QDRANT_URL || '{{qdrant_url}}', apiKey: process.env.QDRANT_API_KEY });
const collection = process.env.QDRANT_COLLECTION || '{{qdrant_collection}}';
await client.upsert(collection, { points: [{ id: '1', vector: [0.1,0.2,0.3], payload: { text: 'hello world' } }] });
const res = await client.search(collection, { vector: [0.1,0.2,0.3], limit: 3 });
console.log(res.map(p => p.payload?.text));
