const { QdrantClient } = require('qdrant-node');
(async () => {
  const client = new QdrantClient({ url: process.env.QDRANT_URL || 'http://localhost:6333' });
  const collection = process.env.QDRANT_COLLECTION || '{{collection}}';
  await client.recreateCollection(collection, { vectors: { size: 3, distance: 'Cosine' } });
  await client.upsert(collection, { points: [{ id: 1, vector: [0.1,0.2,0.3], payload: { text: 'hello' } }] });
  console.log('upserted');
})();
