const amqp = require('amqplib');
(async () => {
  const conn = await amqp.connect(process.env.AMQP_URL || 'amqp://localhost');
  const ch = await conn.createChannel();
  const q = process.env.AMQP_QUEUE || '{{queue}}';
  await ch.assertQueue(q, { durable: false });
  ch.consume(q, msg => { console.log('received', msg.content.toString()); ch.ack(msg); });
})();
