import React from 'react';
import { DndProvider, useDrag } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
function Box(){ const [{isDragging}, drag] = useDrag(()=>({ type:'BOX', collect:(m)=>({isDragging:m.isDragging()}) })); return <div ref={drag} style={{opacity:isDragging?0.5:1}}>Drag me</div>; }
export function App(){ return (<DndProvider backend={HTML5Backend}><Box/></DndProvider>); }
