import React from 'react';
class ErrorBoundary extends React.Component<{children:any},{hasError:boolean}>{ constructor(p:any){ super(p); this.state={hasError:false}; } static getDerivedStateFromError(){ return { hasError:true }; } componentDidCatch(){} render(){ return this.state.hasError ? <div role='alert'>Error</div> : this.props.children; } }
export default function App(){ return <ErrorBoundary><button onClick={()=>{ throw new Error('boom'); }}>Boom</button></ErrorBoundary>; }
