import { useForm, FormProvider, useFormContext } from 'react-hook-form';
function Child(){ const { register } = useFormContext<{ name:string }>(); return <input {...register('name')} />; }
export default function App(){ const methods = useForm<{ name:string }>(); return <FormProvider {...methods}><form><Child/></form></FormProvider>; }
