import React from 'react';
import { useForm } from 'react-hook-form';
import * as yup from 'yup';
import { yupResolver } from '@hookform/resolvers/yup';
const schema = yup.object({ email: yup.string().email().required(), password: yup.string().min(6).required() });
export function LoginYup(){ const { register, handleSubmit, formState:{errors} } = useForm({ resolver: yupResolver(schema) });
  return (<form onSubmit={handleSubmit(console.log)}><input placeholder='email' {...register('email')} />{errors.email?.message as any}<input placeholder='password' type='password' {...register('password')} />{errors.password?.message as any}<button>Login</button></form>); }
