import { useForm } from 'react-hook-form';
import { z } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';
z.setErrorMap((issue, ctx)=> ({ message: issue.code==='invalid_string' ? '無効な文字列' : ctx.defaultError }));
const Schema = z.object({ email: z.string().email() });
type Form = z.infer<typeof Schema>;
export default function FormCmp(){ const { register, handleSubmit, formState:{ errors } } = useForm<Form>({ resolver: zodResolver(Schema) }); return (<form onSubmit={handleSubmit(()=>{})}><input {...register('email')} />{errors.email?.message}</form>); }
