import React from 'react';
import { useForm } from 'react-hook-form';
import { z } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';
const schema = z.object({ email: z.string().email(), password: z.string().min(6) });
export function Login(){ const { register, handleSubmit, formState:{errors} } = useForm({ resolver: zodResolver(schema) });
  return (<form onSubmit={handleSubmit(console.log)}><input placeholder='email' {...register('email')} />{errors.email?.message}<input placeholder='password' type='password' {...register('password')} />{errors.password?.message}<button>Login</button></form>); }
