import { useForm } from 'react-hook-form';
import { z } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';
const schema = z.object({ name: z.string() });
export default function Form(){ const { register, handleSubmit } = useForm({ resolver: zodResolver(schema) }); return <form onSubmit={handleSubmit(()=>{})}><input {...register('name')} /><button>OK</button></form>; }
