import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';
const resources = { en: { t: { hello: 'Hello' } }, ja: { t: { hello: 'こんにちは' } } };
i18n.use(initReactI18next).init({ resources, lng: 'en', ns: ['t'] });
export default i18n;

import React from 'react';
import './i18n';
import { useTranslation } from 'react-i18next';
export function App(){ const { t, i18n } = useTranslation('t'); return (<div><p>{t('hello')}</p><button onClick={()=>i18n.changeLanguage('ja')}>JA</button><button onClick={()=>i18n.changeLanguage('en')}>EN</button></div>); }
