import React from 'react';
import { IntlProvider, useIntl } from 'react-intl';
const messages = { en: { hello: 'Hello' }, ja: { hello: 'こんにちは' } };
export function App(){ const [lng,setLng]=React.useState('en'); const intl = useIntl as any; return (<IntlProvider locale={lng} messages={messages[lng]}><p>{intl?.formatMessage? intl.formatMessage({id:'hello'}): 'Hello'}</p><button onClick={()=>setLng('ja')}>JA</button><button onClick={()=>setLng('en')}>EN</button></IntlProvider>); }
