import { QueryClient, QueryClientProvider, useQuery } from '@tanstack/react-query';
const qc = new QueryClient();
function Hello(){ const { data } = useQuery({ queryKey:['hi'], queryFn: async()=> 'hello' }); return <div>{data}</div>; }
export default function App(){ return <QueryClientProvider client={qc}><Hello/></QueryClientProvider>; }
