import React, { Suspense } from 'react';
function wrap(p){ let s='pending', v; const pr = p.then(r=>{ s='success'; v=r; }, e=>{ s='error'; v=e; }); return { read(){ if(s==='pending') throw pr; if(s==='error') throw v; return v; } }; }
const resource = wrap(fetch('https://example.com').then(r=>r.text()));
function Inner(){ return <div>{resource.read()}</div>; }
export default function App(){ return <Suspense fallback={<div>...</div>}><Inner/></Suspense>; }
