import React from 'react';
import { QueryClient, QueryClientProvider, useQuery } from '@tanstack/react-query';
const qc = new QueryClient();
function Data(){ const { data } = useQuery({ queryKey:['zen'], queryFn: async()=> fetch('https://api.github.com/zen').then(r=>r.text()) }); return <pre>{data}</pre>; }
export function App(){ return <QueryClientProvider client={qc}><Data/></QueryClientProvider>; }
