const Redis = require('ioredis');
const r = new Redis(process.env.REDIS_URL);
(async () => {
  const key = process.env.REDIS_KEY || '{{key}}';
  await r.call('JSON.SET', key, '$', JSON.stringify({ msg: 'hello' }));
  const val = await r.call('JSON.GET', key, '$');
  console.log(val && JSON.parse(val));
  process.exit(0);
})();
