const Redis = require('ioredis');
const r = new Redis(process.env.REDIS_URL);
setInterval(()=> r.publish(process.env.CHANNEL || '{{channel}}', 'hello'), 1000);

const Redis = require('ioredis');
const r = new Redis(process.env.REDIS_URL);
r.subscribe(process.env.CHANNEL || '{{channel}}');
r.on('message', (_ch, msg) => console.log(msg));
