import { createClient } from 'redis';
const client = createClient({ url: process.env.REDIS_URL || '{{redis_url}}' });
await client.connect();
try { await client.sendCommand(['FT.DROPINDEX', process.env.REDIS_INDEX || '{{index_name}}']); console.log('dropped'); } catch (e) { console.log('drop failed', e?.message); }
await client.quit();
