import { Kafka } from 'kafkajs';
const kafka = new Kafka({ clientId: 'demo', brokers: [process.env.KAFKA_BROKER||'localhost:9092'] });
const p = kafka.producer();
await p.connect();
await p.send({ topic: 'events', messages:[{ value:'hello' }] });
await p.disconnect();
console.log('sent');
