const { Kafka } = require('kafkajs');
const kafka = new Kafka({ clientId: 'app', brokers: (process.env.BROKERS||'localhost:9092').split(',') });
(async()=>{ const p = kafka.producer(); await p.connect(); await p.send({ topic: process.env.TOPIC || '{{topic}}', messages:[{ value: 'hello' }] }); await p.disconnect(); })();
