import { Form, useActionData } from '@remix-run/react';
import type { ActionFunctionArgs } from '@remix-run/node';
import { json } from '@remix-run/node';
export async function action({ request }: ActionFunctionArgs){ const fd = await request.formData(); return json({ ok: true, name: fd.get('name') }); }
export default function Index(){ const data = useActionData<typeof action>(); return (<main><h1>Form</h1><Form method="post"><input name="name" placeholder="name"/><button>Send</button></Form>{data? <pre>{JSON.stringify(data)}</pre>:null}</main>); }
